package com.kaban.grasshopper;import javax.microedition.khronos.egl.EGLConfig;import javax.microedition.khronos.opengles.GL10;import android.content.res.AssetManager;import android.content.Context;import android.app.Activity;import android.content.pm.ActivityInfo;import android.opengl.GLSurfaceView;import android.os.Bundle;import android.view.View;import android.view.Window;class Native{    static    {        System.loadLibrary("grasshopper");    }	public native static void init(AssetManager mgr);	public native static void draw();	public native static void resize(int w, int h);}class Renderer implements GLSurfaceView.Renderer{	private AssetManager _manager; 	public Renderer(Context context)    {        super();        _manager = context.getAssets();    }	@Override public void onDrawFrame(GL10 no_use) {Native.draw();}	@Override public void onSurfaceCreated(GL10 no_use, EGLConfig c)  {Native.init(_manager);}	@Override public void onSurfaceChanged(GL10 no_use, int w, int h) {Native.resize(w, h);}}public class Main extends Activity{	private GLSurfaceView _surface;	@Override public void onCreate(Bundle savedState)	{		super.onCreate(savedState);		setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_PORTRAIT);		requestWindowFeature(Window.FEATURE_NO_TITLE);		setContentView(R.layout.main);		_surface = (GLSurfaceView) findViewById(R.id.glview);		_surface.setEGLContextClientVersion(2);		_surface.setEGLConfigChooser(true);		_surface.setRenderer(new Renderer(this));	}	}